mapper.select:
SELECT a.*, u.nickname, 
	(SELECT COUNT(*) from Articleempathy e WHERE e.articleId = a.id) empathy 
FROM Article a 
LEFT JOIN user u ON a.userId = u.id
WHERE a.deleted = 'N' AND a.boardId = #{boardId}
ORDER BY a.id;

SELECT a.*, u.nickname, 
	(SELECT COUNT(*) from Articleempathy e WHERE e.articleId = a.id) empathy 
FROM Article a 
LEFT JOIN user u ON a.userId = u.id
WHERE a.deleted = 'N' AND a.id = #{id}
ORDER BY a.id;
