mapper.select:
SELECT a.*, u.nickname, 
	(SELECT COUNT(*) from Articleempathy e WHERE e.articleId = a.id) empathy 
FROM Article a 
LEFT JOIN user u ON a.userId = u.id
WHERE a.deleted = 'N' AND a.boardId = #{boardId}
ORDER BY a.id;

SELECT a.*, u.nickname, 
	(SELECT COUNT(*) from Articleempathy e WHERE e.articleId = a.id) empathy 
FROM Article a 
LEFT JOIN user u ON a.userId = u.id
WHERE a.deleted = 'N' AND a.categoryId = #{categoryId}
ORDER BY a.id;

SELECT a.*, u.nickname, 
	(SELECT COUNT(*) from Articleempathy e WHERE e.articleId = a.id) empathy 
FROM Article a 
LEFT JOIN user u ON a.userId = u.id
WHERE a.deleted = 'N' AND a.userId = #{userId}
ORDER BY a.id;

SELECT a.*, u.nickname, 
	(SELECT COUNT(*) from Articleempathy e WHERE e.articleId = a.id) empathy 
FROM Article a 
LEFT JOIN user u ON a.userId = u.id
WHERE a.deleted = 'N' AND a.id = #{id}
ORDER BY a.id;

mapper.update:
UPDATE Article 
SET hits = hits + 1 
WHERE id = #{id}

UPDATE Article
SET title = #{article.title}, contents = #{article.contents}, 
anonymous = #{article.anonymous}, categoryId = #{article.categoryId}
WHERE id=#{id}

UPDATE Article
SET deleted = "Y"
WHERE id = #{id}
